div.script:
function confirmPlanning(planningId) {
    if (confirm('Confirmer cette planification ?')) {
        fetch('/monitor/planning/confirm', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                'planning_id': planningId
            })
        }).then(response => {
            if (response.ok) {
                location.reload();
            }
        });
    }
}

function startPlanning(planningId) {
    if (confirm('Commencer cette intervention ?')) {
        fetch('/monitor/planning/start', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                'planning_id': planningId
            })
        }).then(response => {
            if (response.ok) {
                location.reload();
            }
        });
    }
}

function completePlanning(planningId) {
    if (confirm('Terminer cette intervention ?')) {
        fetch('/monitor/planning/complete', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                'planning_id': planningId
            })
        }).then(response => {
            if (response.ok) {
                location.reload();
            }
        });
    }
}

function postponePlanning(planningId) {
    if (confirm('Reporter cette planification ?')) {
        fetch('/monitor/planning/postpone', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                'planning_id': planningId
            })
        }).then(response => {
            if (response.ok) {
                location.reload();
            }
        });
    }
}

function cancelPlanning(planningId) {
    if (confirm('Annuler définitivement cette planification ?')) {
        fetch('/monitor/planning/cancel', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                'planning_id': planningId
            })
        }).then(response => {
            if (response.ok) {
                location.reload();
            }
        });
    }
}

function requestSubstitute(planningId) {
    // Ouvrir une modal ou rediriger vers un formulaire de demande de remplaçant
    window.location.href = '/monitor/planning/substitute/' + planningId;
}
